package ngay3;

public class bai7 {
//	7. In ra màn hình bảng cửu chương (8 bảng từ 2 đến 9).
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=2;i<=9;i++) {
			for(int j=1;j<=10;j++) {
				System.out.print(i+"x"+j+"="+i*j+" ; ");
			}
			System.out.println("");
		}
	}

}
